        function initPagination(item){
            
            var itemsPerPage = 12;
            var numItems = item.length;
            var numPages = Math.ceil(numItems / itemsPerPage);
            var currentPage = 1;

            if (numItems <= itemsPerPage) {
                $('#hotels_pagination').hide();
            } else {
                $('#hotels_pagination').html('');
                $('#hotels_pagination').show();
                for (var i = 1; i <= numPages; i++) {
                    $('<a href="#"></a>').text(i).appendTo('#hotels_pagination').on('click', function() {
                        currentPage = parseInt($(this).text());
                        var start = (currentPage - 1) * itemsPerPage;
                        var end = start + itemsPerPage;

                        item.hide().slice(start, end).show();
                        $(this).addClass('active').siblings().removeClass('active');
                        return false;
                    });
                }
    
                item.hide().slice(0, itemsPerPage).show();

                $('<a href="#" id="prev" class="pg_controls btn_pg_prev"><i class="fas fa-angle-left"></i>').prependTo('#hotels_pagination').on('click', function() {
                    if (currentPage > 1) {
                      currentPage--;
                      var start = (currentPage - 1) * itemsPerPage;
                      var end = start + itemsPerPage;
                      item.hide().slice(start, end).show();
                      $('#hotels_pagination').find('a').eq(currentPage).addClass('active').siblings().removeClass('active');
                    }
                    return false;
                  });
                  $('<a href="#" id="next" class="pg_controls btn_pg_next"><i class="fas fa-angle-right"></i></a>').appendTo('#hotels_pagination').on('click', function() {
                    if (currentPage < numPages) {
                      currentPage++;
                      var start = (currentPage - 1) * itemsPerPage;
                      var end = start + itemsPerPage;
                      item.hide().slice(start, end).show();
                      $('#hotels_pagination').find('a').eq(currentPage).addClass('active').siblings().removeClass('active');
                    }
                    return false;
                  });

                  $('#hotels_pagination').find('a').eq(1).addClass('active');
            }
        }
    
