        function initShopLocation(shops, pinImg, map, directionsRenderer) {
            shops.forEach((item, index) => {
                let position = { lat: +(item.latitude), lng: +(item.longitude) };
                let infoCard =
                    `<div class="text-dark text-left" >
                                <div class="">
                                    <p class=" info-w-shopname font-weight-bold text-center text-uppercase hotels_name " >${item.name} </p>
                                    <div class="my-3 hotels_desc" >
                                        <p><i class="fas fa-phone-alt"></i><a href="tel:${item.phone}">${item.phone}</a></p>
                                        <p><i class='fas fa-map-marked-alt'></i><span> ${item.address}, ${item.city}</span></p>
                                    </div>
                                    <div class="w-100"><button class="btn btn-outline-secondary btn-sm my-3 d-block mx-auto " id="requestPath">Demander l'itinéraire  </button> </div>
                                </div>
                        </div>
    
                        `
                let infoWindow = new google.maps.InfoWindow({
                    content: infoCard,
                });
    
                let marker = new google.maps.Marker({
                    map: map,
                    position: position,
                    title: item.name,
                    icon: pinImg,
                    size: new google.maps.Size(20, 32)
                });
    
                marker.addListener("click", () => {
                    updateInfoWindow(infoWindow, position)
    
                    infoWindow.open({
                        anchor: marker,
                        map,
                        shouldFocus: false,
                    });
                });
            })
        }
    
